INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (346, 'AgEcon Search', 'http://ageconsearch.umn.edu', '1932', '', 'University of Minnesota', '', 1, 'AgEcon Search is a free, open access repository of full-text scholarly literature in agricultural and applied economics, including working papers, conference papers, and journal articles.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (351, 'Scirus', 'http://scirus.com/', '', '', 'Elsevier', '', 1, 'Scirus searches over 415 million science-specific web pages for scientific, scholarly, technical and medical data.  It covers the latest technical reports, peer-reviewed articles, patents, pre prints and journals. Links in the search results will often lead to OSU Libraries\\'' subscriptions to full-text articles. ');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (205, 'CARINDEX', 'http://www.inasp.info/iah/carindex.html', '1983', 'present', 'University of West Indies Library', 'http://www.mainlib.uwi.tt/', 1, 'CARINDEX is an index to the contents of some 70 journals, published in the Caribbean region. It also includes theses and papers presented at conferences. It covers publications in the social sciences and humanities from 1983 onwards. Full bibliographical details are provided. Each record contains an abstract of content. The full text of items is not available.  CARINDEX can be configured to search in Portugese, Spanish or English languages.\r\n');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (28, 'Chemical Abstracts (SciFinder Scholar)', 'http://osulibrary.oregonstate.edu/research/guides/sfs.html', '1907', 'present', 'SciFinder Scholar', '/research/databases/scifind.htm', 1, 'SciFinder Scholar provides electronic searching of Chemical Abstracts (CA) and the CAS Registry file. CA is the world\\''s largest and most up-to-date collection of chemical information, with more than 20 million abstracts of journal articles, patents, and more. CAS Registry contains more than 24,000,000 substance records, including more than 16,000,000 organic and inorganic substances and 4,000,000 biosequences. The Registry is the world\\''s largest collection of organic substance records. <BR><I>1907-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (491, 'GreenFILE', 'http://search.ebscohost.com/login.aspx?authtype=ip,uid&profile=ehost&defaultdb=8gh', '', '', 'EBSCOhost', '', 1, 'A freely accessible research database focusing on the relationship between human beings and the environment, with well-researched but accessible information on topics ranging from global warming to recycling to alternate fuel sources and beyond. Comprised of scholarly and general interest titles, as well as government documents and reports, GreenFILE offers a unique perspective on the positive and negative ways humans affect the ecology. Drawing on the connection between the environment and disciplines such as agriculture, education, law, health and technology, GreenFILE will serve as an informative resource for anyone concerned about the issues facing our planet.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (243, 'PubMed (also see MEDLINE)', 'http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=PubMed', '1966', 'present', 'PubMed (Free access from the National Library of Medicine)', '', 1, 'Index to research     articles covering all areas of medicine including clinical and experimental     medicine; dentistry; health care administration; microbiology; nursing; paramedical     professions; pathology; pharmacology; physiology; reproductive biology; psychiatry;     psychology; toxicology; and veterinary medicine. PubMed is     direct from the National Library of Medicine web site.<br>    Abstracts and index:    <i>1966-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (44, 'EDGAR (SEC filings & forms)', 'http://www.sec.gov/edgar.shtml', '1993', 'present', 'SEC', '', 1, 'EDGAR, the Electronic Data Gathering, Analysis, and Retrieval system, offersaccess to time-sensitive corporate information.  With a few exceptions, allpublic domestic companies have been required to file Form 10-K or Form 10-KSBwith EDGAR since May 6, 1996.  Companies may also voluntarily submit suchdocuments as annual reports, security ownership and transaction reports filed bycorporate insiders, and notices of proposed sales of securities.  Foreigncompanies will be required to file documetns on EDGAR beginning November 4,2002.<br><i>1993</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (193, 'GPO Access', 'http://www.gpoaccess.gov/', '', '', 'U.S. Government Printing Office', '', 1, ' GPO Access is a service of the U.S. Government Printing Office that provides free electronic access to a wealth of important information products produced by the Federal Government. The information provided on this site is the official, published version and the information retrieved from GPO Access can be used without restriction, unless specifically noted. This free service is funded by the Federal Depository Library Program and has grown out of Public Law 103-40, known as the Government Printing Office Electronic Information Enhancement Act of 1993. \r\n');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (81, 'Librarians\\'' Index to the Internet', 'http://sunsite.berkeley.edu/InternetIndex', '', '', 'Library of California', '', 1, '');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (171, 'MEDLINE Plus', 'http://medlineplus.gov', '', '', 'National Institutes of Health', '', 1, 'MEDLINEplus will direct you to information to help answer health questions. MEDLINEplus brings together, by health topic, authoritative information from NLM, the National Institutes of Health (NIH), other government, non-profit and other health-related organizations.\r\nPreformulated MEDLINE searches are included in MEDLINEplus and give easy access to the medical research literature');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (89, 'MEDLINE (NLM Gateway)', 'http://gateway.nlm.nih.gov/gw/Cmd', '1950', 'present', 'National Library of Medicine Gateway', '', 1, '   Index to research     articles covering all areas of medicine including clinical and experimental     medicine; dentistry; health care administration; microbiology; nursing; paramedical     professions; pathology; pharmacology; physiology; reproductive biology; psychiatry;     psychology; toxicology; and veterinary medicine. Direct from the National Library of Medicine web site.<br>    Abstracts and index:  <i>1966-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (90, 'MEDLINE (PubMed)', 'http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=PubMed', '1950', 'present', 'PubMed (Free access from the National Library of Medicine)', '', 1, '   Index to research     articles covering all areas of medicine including clinical and experimental     medicine; dentistry; health care administration; microbiology; nursing; paramedical     professions; pathology; pharmacology; physiology; reproductive biology; psychiatry;     psychology; toxicology; and veterinary medicine. PubMed is     direct from the National Library of Medicine web site.<br>    Abstracts and index:    <i>1966-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (221, 'TRIS Online', 'http://trisonline.bts.gov/sundev/search.cfm', '1999', '', 'Transportation Research Board', '', 1, 'TRIS Online provides access to almost 450,000 records of published transportation research through a user-friendly searchable database. Over 470 serial publications are regularly indexed and abstracted for the database. TRIS Online includes material indexed and abstracted by TRB as well as the material entered by the Transportation Libraries at the University of California at Berkeley and Northwestern University. Records from the PATH Database (a database on intelligent transportation systems produced at the University of California, Berkeley) are now included in TRIS Online. Currently the Research in Progress files and the international material in the International Transport Research Documentation (ITRD) Database are not available as part of TRIS Online. \r\n');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (100, 'Nutrition Abstracts (Part of CAB Abstracts)', 'http://0-217.154.120.06.oasis.oregonstate.edu/cabdirect/searchform-advancedsearch.nsp', '1984', 'present', 'CABI', '', 1, 'A subset of CAB Abstracts');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (104, 'Oregonian', 'http://osulibrary.oregonstate.edu/research/research/oregonian.html', '1987', 'present', 'LexisNexis & OregonLive', '', 1, 'The statewide license to the Oregonian has ended.  Access to the Oregonian is still available through LexisNexis Academic and the Oregonian''s web site.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (109, 'Patent Database', 'http://www.uspto.gov/patft/index.html', '1976', 'present', 'U. S. Patent and Trademark Office', '', 1, ' Access to bibliographic and full-text patent databases. <br><i>1976-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (125, 'SciFinder Scholar (Chemical         Abstracts)', 'http://osulibrary.oregonstate.edu/research/guides/sfs.html', '1907', 'present', 'CAS', '', 1, 'SciFinder Scholar provides electronic searching of Chemical Abstracts (CA) and the CAS Registry file. CA is the world\\''s largest and most up-to-date collection of chemical information, with more than 20 million abstracts of journal articles, patents, and more. CAS Registry contains more than 24,000,000 substance records, including more than 16,000,000 organic and inorganic substances and 4,000,000 biosequences. The Registry is the world\\''s largest collection of organic substance records. <BR><I>1907-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (126, 'SCOLA schedule', 'http://www.scola.org/eoneCommerce/FileServer?file=1-200502.pdf&type=pdf', '', '', 'SCOLA', '', 1, 'SCOLA is a non-profit educational consortium that receives and re-transmits television programming from nearly seventy different countries in their original languages. These programs are transmitted via satellite to schools, colleges, universities, government and military installations, cable TV systems, independent TV stations, businesses and private individuals throughout North America and much of the Northern Western Hemisphere. SCOLA currently operates three 24-hour Satellite Channels.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (127, 'SEC filings & forms (EDGAR)', 'http://www.sec.gov/edgar.shtml', '1993', 'present', 'SEC', '', 1, 'EDGAR, the Electronic Data Gathering, Analysis, and Retrieval system, offers access to time-sensitive corporate information.  With a few exceptions, all public domestic companies have been required to file Form 10-K or Form 10-KSB with EDGAR since May 6, 1996.  Companies may also voluntarily submit such documents as annual reports, security ownership and transaction reports filed by corporate insiders, and notices of proposed sales of securities.  Foreign companies will be required to file documetns on EDGAR beginning November 4, 2002.<br><i>1993</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (128, 'Shakespeare Database:         The Complete Works of William Shakespeare', 'http://the-tech.mit.edu/Shakespeare/', '', '', 'MIT', '', 1, 'Maintained at MIT, The Complete Works of William Shakespeare can be searched by keyword. Categories of works and individual works may also be searched.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (132, 'Social Sciences in Forestry', 'http://forestry.lib.umn.edu/bib/SSiF.phtml', '1985', 'present', 'Minnesota Forestry Library', '', 1, 'This database indexes publications in 41 subject areas relating the social sciences to forestry. Among them are: legislation, policy and planning, management, economic development, investment and finance, and more.<br><i>1985-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (139, 'Statistical Abstract', 'http://www.census.gov/compendia/statab/', '', '2006', 'US Dept of Commerce', '', 1, 'A collection of statistics on social and economic conditions in the United States. Selected international data are also included. The Abstract is also a guide to sources of other data from the Census Bureau, other Federal agencies, and private organizations.<br><b>Full-text and data:</b>  <i>2006</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (146, 'Trademark Electronic Search System (TESS)', 'http://tess2.uspto.gov/bin/gate.exe?f=tess&state=gsugpe.1.1', '', 'Current to within last two to four months', 'U. S. Patent and Trademark Office', '', 1, ' This page is the starting point for the USPTO\\''s free trademark database, which includes the full bibliographic text of pending and registered trademarks.<br><i>Current to within last two to four months</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (147, 'Trail Planning, Construction, and Maintenance', 'http://forestry.lib.umn.edu/bib/trls.phtml', '1984', 'present', 'University of Minnesota Forestry Library', '', 1, 'Indexes publications relating to the planning, design, construction, and maintenance of all types of trails. Includes trail use studies.<br><i>1984-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (148, 'Tropical Forest Conservation and Development', 'http://forestry.lib.umn.edu/bib/trps.phtml', '1982', 'present', 'University of Minnesota Forestry Library', '', 1, 'Subjects indexed include: Tropical forest resources; deforestation; conservation; indigenous peoples; management; policy; trade and industrial development; nontimber forest products; research, education, and training; and history. For related subjects, see the online index Social Sciences in Forestry.<br><i>1982-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (152, 'Urban Forestry', 'http://forestry.lib.umn.edu/bib/urban.phtml', '1980', 'present', 'University of Minnesota Forestry Library', '', 1, 'Indexes publications relating to the history of urban forestry; urban forest legislation; the benefits of urban forests, selection and planting of trees; maintenance of the urban forest; planning and management; and urban forestry programs.<br><i>1980-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (155, 'USPTO Patent Database', 'http://www.uspto.gov/patft/index.html', '1976', 'present', 'U. S. Patent and Trademark Office', '', 1, ' Access to bibliographic and full-text patent databases. <br><i>1976-present</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (156, 'USPTO Trademark Database', 'http://tess2.uspto.gov/bin/gate.exe?f=tess&state=gsugpe.1.1', '', 'Current to within last two to four months', 'U. S. Patent and Trademark Office', '', 1, ' This page is the starting point for the USPTO\\''s free trademark database, which includes the full bibliographic text of pendingand registered trademarks.<br><i>Current to within last two to four months</i>');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (198, 'Sport Fish Division Reports (ADF&G)', 'http://www.sf.adfg.state.ak.us/statewide/divreports/html/SportFishDivReports_VSearchForm.cfm', '1987', '', 'Alaska Department of Fish and Game', '', 1, '');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (330, 'Descriptions of Fungi and Bacteria', 'http://0-217.154.120.06.oasis.oregonstate.edu/FUNGIBACTERIA/searchform-advancedsearch.nsp', '1964', '', 'CABI', '', 1, 'Descriptions of Fungi and Bacteria 1964-date. Provides full-text descriptions for 40  organism per year.  Each entry includes a detailed description of the pathogen, illustration, information about the disease caused, geographical distribution, transmission and references to the key literature.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (471, 'ERAM (Electronic Research Archive for Mathematics)', 'http://www.emis.de/MATH/JFM/JFM.html', '1868', '1942', 'European Mathematical Society', '', 1, 'Description:  ERAM (also known as the Jarhbuch Project) provides in electronic format the "Jahrbuch Ã¼ber die Fortschritte der Mathematik" (JFM) along with a digital archive of the most important mathematical publications from 1868-1942.  Contains links to over 17, 700 <A HREF="http://www.emis.de/MATH/JFM/update.html">online documents</A> including articles, texts in historical math collections, dissertations and scripts.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (236, 'Energy Citations Database', 'http://www.osti.gov/energycitations/', '1948', '', 'Department of Energy', '', 1, 'Energy Citations contains bibliographic records for energy and energy-related scientific and technical information from the Department of Energy (DOE) and its predecessor agencies, the Energy Research & Development Administration (ERDA) and the Atomic Energy Commission (AEC). The Database provides access to DOE publicly available citations from 1948 through the present, with continued growth through regular updates.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (237, 'GrayLit Network', 'http://www.osti.gov/graylit', '1974', '', 'Department of Energy', '', 1, 'Developed by the Department of Energy''s Office of Scientific and Technical Information (OSTI), in collaboration with DOD/DTIC, NASA, and EPA, the GrayLIT Network is a portal for technical report information generated through federally funded research and development projects.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (240, 'Treesearch: USDA Forest Service Research Publications', 'http://www.treesearch.fs.fed.us/', '2004', '', 'U.S. Forest Service', '', 1, 'Treesearch contains peer reviewed, published research findings of  the U.S. Forest Service. Includes not only agency publications, but also articles from journals, conference proceedings, and books whose authors are employed by the Forest Service.  Includes many retrospective titles and will become a more comprehensive resource over time.  \r\n');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (270, 'Bibliography on Cold Regions Science and Technology', 'http://www.coldregions.org/dbtw-wpd/cold/coldA.htm', '1730', '', 'American Geological Institute', '', 1, 'The Bibliography on Cold Regions Science and Technology includes references to scientific and engineering research related to material and operations in a winter battlefield, the nature and impact of cold on facilities and activities, cold-related environmental problems, and the impact of human activity on cold environments. ');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (271, 'Antarctic Bibliography', 'http://www.coldregions.org/dbtw-wpd/coldy/coldyBn.htm', '1918', '', 'American Geological Institute', '', 1, 'The Antarctic Bibliography covers all disciplines related to the region including biological and geological sciences, medical sciences, meteorology, oceanography, atmospheric and terrestrial physics, expeditions, logistics equipment and supplies, and tourism. ');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (285, 'Citeseer (Computer and Information Science Papers)', 'http://citeseer.ist.psu.edu/', '', '', 'Pennsylvania State University', '', 1, 'CiteSeer is a scientific literature digital library and search engine that focuses primarily on the literature in computer and information science. CiteSeer aims to improve the dissemination and feedback of the scientific literature and to provide improvements in functionality, usability, availability, cost, comprehensiveness, efficiency, and timeliness in the access of scientific and scholarly knowledge. CiteSeer was developed at the NEC Research Institute by Steve Lawrence, Lee Giles and Kurt Bollacker. It is currently hosted at Penn State''s School of Information Sciences and Technology under the direction of Professor Lee Giles.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (286, 'SMEALSearch (Academic Business Literature Digital Library)', 'http://smealsearch2.psu.edu/index.html/', '', '', 'Pennsylvania State University', '', 1, 'SMEALSearch is a niche search engine that searches the web and catalogs academic articles as well as commercially produced articles and reports that address any branch of Business. The search engine crawls websites of universities, commercial organizations, research institutes and government departments to retrieve academic articles, working papers, white papers, consulting reports, magazine articles, and published statistics and facts.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (287, 'Current Research Information Service (CRIS)', 'http://cris.csrees.usda.gov/', '', '', 'USDA', 'http://www.usda.gov/', 1, 'CRIS is the U.S. Department of Agriculture''s (USDA) documentation and reporting system for ongoing and recently completed research projects in agriculture, food and nutrition, and forestry.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (288, 'E. V. Komarek Fire Ecology Database', 'http://www.talltimbers.org/fedb-intro.html', '1987', '', 'Tall Timbers Research Station', '', 1, 'The E.V. Komarek Fire Ecology Database is comprised of bibliographic citations to a broad collection of fire ecology literature.  Historical and current works are included in the database some with abstracts.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (506, 'Oregon Encyclopedia', 'http://www.oregonencyclopedia.org/', '', '', 'Portland State University', '', 1, 'The Oregon Encyclopedia is an authoritative compendium of information on Oregon people, places, events, and institutions. ');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (304, 'Searchable Ornithological Research Archives (SORA)', 'http://elibrary.unm.edu/sora/index.php', '1884', '2000', 'University of New Mexico', '', 1, 'The SORA project (http://elibrary.unm.edu/sora) is an open access electronic journal archive and is the product of a collaboration between the American Ornithologists Union, the Cooper Ornithological Society, the Association of Field Ornithologists, the Wilson Ornithological Society and the University of New Mexico libraries and IT department. This archive provides access to an extensive Ornithological literature of international scope, and detailed material documenting the history of Ornithology in North America over the last 120 years. The content of this site includes the following titles: The Auk (1884-1999), The Condor (1899-2000), The Journal of Field Ornithology (1930-1999), The North American Bird Bander (1976-2000), Pacific Coast Avifauna (1900-1974), Studies in Avian Biology (1978-1999), and The Wilson Bulletin (1889-1999). Also available with browse-only capability is Ornithologia Neotropical; we hope to be able to index this journal soon, and include searching across its contents. ');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (314, 'Homeland Security Digital Library', 'https://www.hsdl.org/', '', '', 'U.S. Department of Homeland Security', '', 1, 'The HSDL collection provides quick access to important U.S. policy documents, presidential directives, and national strategy documents as well as specialized resources such as theses and reports from various universities, organizations and local and state agencies. The resources are reviewed and selected by a team of homeland security researchers and organized in a unique homeland security taxonomy. HSDL content includes state-of-the-art multi-media offerings and other valuable assets identified by CHDS master\\''s degree participants and instructors.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (320, 'Northwest Digital Archives (NWDA)', 'http://nwda.wsulibs.wsu.edu/', '', '', 'NWDA', 'http://nwda.wsulibs.wsu.edu/', 1, 'Northwest Digital Archives (NWDA) provides enhanced access to archival and manuscript materials in Idaho, Montana, Oregon, and Washington through a union database of Encoded Archival Description (EAD) finding aids. NWDA was established in June 2002 through grants from the National Endowment for the Humanities (NEH) and the National Historical Publications and Records Commission (NHPRC). Phase I was successfully completed in December 2004. The database currently contains more than 2,500 finding aids.\r\n');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (326, 'Ageline (AARP)', 'http://www.aarp.org/research/ageline/', '1978', '', 'AARP', '', 1, 'AgeLine abstracts the literature of social gerontology as well as aging-related research from psychology, sociology, social work, economics, public policy, and the health sciences. It covers aging-related issues for professionals in aging services, health, business, law, and mental health. AgeLine also includes selected consumer content. 1978-present.<br><br><b>Concurrent Users:</b> 50\r\n');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (356, 'Encyclopedia of Earth', 'http://www.eoearth.org', '', '', 'Environmental Information Coalition', '', 1, 'An electronic reference about the Earth, its natural environments, and their interaction with society. The Encyclopedia is a free, fully searchable collection of articles written by scholars, professionals, educators, and experts who collaborate and review each other\\''s work. The articles are written in non-technical language and will be useful to students, educators, scholars, professionals, as well as to the general public.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (361, 'Google Scholar', 'http://scholar.google.com', 'Unknown', '', 'Google', '', 1, 'Uses the Google search engine to search for scholarly materials from around the world such as peer-reviewed papers, theses, books, preprints, abstracts and technical reports.  Google Scholar searches a variety of academic publishers, professional societies, preprint repositories and universities. Some articles are available in full-text through links to OSU''s journal subscriptions. ');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (366, 'Intute', 'http://www.intute.ac.uk/', 'Unknown', '', 'University of Manchester', '', 1, 'Intute is a free online service providing you with access to the very best Web resources for education and research. The service is created by a network of UK universities and partners. Subject specialists select and evaluate the websites in our database and write high quality descriptions of the resources. The database contains 119358 records.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (441, 'Latin American Periodicals Tables of Contents', 'http://lanic.utexas.edu/larrp/laptoc.html', '', '', 'Latin Americanist Research Resources Project', '', 1, 'A searchable Web database that provides access to the tables of contents of more than 950 journals, primarily in the humanities and social sciences, published in Latin America.');
INSERT INTO `dods` (`id`, `title`, `url`, `startdate`, `enddate`, `provider`, `providerurl`, `proxy`, `descr`) VALUES (446, 'Middle English Dictionary', 'http://quod.lib.umich.edu/m/med/    ', '1100', '1500', 'University of Michigan', '', 1, 'The print MED, completed in 2001, has been described as "the greatest achievement in medieval scholarship in America." Its 15,000 pages offer a comprehensive analysis of lexicon and usage for the period 1100-1500, based on the analysis of a collection of over three million citation slips, the largest collection of this kind available. This electronic version of the MED preserves all the details of the print MED, but goes far beyond this, by converting its contents into an enormous database, searchable in ways impossible within any print dictionary. ');
